import React from 'react';
import { Link } from 'react-router-dom';

var usersinfo = [];
export default function Login() {

    var usersinfo = [];
document.getElementById('btn1').onclick=function() {
    // alert();
    var emailid = document.getElementById('txt1').value;
    var pass = document.getElementById('txt2').value;
    var name = document.getElementById('txt3').value;

    // Email-ID
    var reg_exp = /^([a-zA-Z0-9]([a-zA-Z0-9\-]+)?).([a-zA-Z]{2,})(\.[a-zA-Z]{2,})$/;
    var emailresult = reg_exp.test(emailid);
    console.log(emailresult);
    if(!emailresult){
        txt1.nextElementSibling.style.display='inline-block';
    }else{
        txt1.nextElementSibling.style.display='none';
    }
    
    // password
    var reg_exp = /^(?=.*[0-9])(?=.*[@#\$\.\-])(?=.*[a-z])(?=.*[A-Z]).{4,8}$/;
    var passresult = reg_exp.test(pass);
    console.log(passresult);
    if(!passresult){
        txt2.nextElementSibling.style.display='inline-block';
    }else{
        txt2.nextElementSibling.style.display='none';
    }

    // username
    var reg_exp = /^[a-zA-Z]([a-zA-Z ]+)?[a-zA-z]$/;
    var usernameresult = reg_exp.test(name);
    console.log(usernameresult);
    if(!usernameresult){
        txt3.nextElementSibling.style.display='inline-block';
    }else{
        txt3.nextElementSibling.style.display='none';
    }



    //Create Object
    var obj = {
        email: emailid, password: pass, username: name
    };
    //compare user input and exist array data 
    // and dosnt exist then data push into the array
    var users = usersinfo.filter(val => val.email == emailid);
    console.log(users, users.length);

    if(users.length>0){
        console.log('email exists');
        document.getElementById('para1').innerHTML='Email-id alredy exist';

    }         
    else if(emailresult==true,passresult==true,usernameresult==true){
        usersinfo.push(obj);
        console.log(usersinfo);
        console.log('email added');
        document.getElementById('para1').innerHTML='Email-id Added';
        
    }

}

// login Button

document.querySelector("#btn2").onclick=function(){
    var loginmail=document.getElementById('text1').value;
    var loginpass=document.getElementById('text2').value;

    console.log(loginmail,loginpass);

    var ans=usersinfo.filter(val=>val.email==loginmail && val.password==loginpass)
    console.log(ans);
    if(ans.length>0){
        console.log('success');
        document.getElementById('para2').innerHTML='Success';
    }
    else{
        console.log('invalid mail id and password');
        document.getElementById('para2').innerHTML='Invalid Email or Password'
    }
}

  return (
    <div>
    <h1>Register</h1>
    <input type="text" id="txt1" placeholder="Email"/>  
    <span>Enter Valid Email-ID</span> <br/>


    <input type="text" id="txt2" placeholder="Password"/> 
    <span>Enter Valid Password</span><br/>


    <input type="text" id="txt3" placeholder="Username"/>
    <span>Enter Valid UserName</span> <br/>


    <button id="btn1">Sign Up</button> <br/>
    <p id="para1"></p>

<hr/>

<h2>Login</h2>
    <input type="text" id="text1" placeholder="Email-ID"/><br/>
    <input type="text" id="text2" placeholder="Password"/><br/>
    <button id="btn2">Login</button>
    <p id="para2"></p>





    </div>
  )
}
